const DAY_MS = 1000 * 60 * 60 * 24;
const DAILY_SEED = 773401;
const BANK_VERSION = 3;

function mulberry32(seed: number) {
  let t = seed + 0x6d2b79f5;
  return function () {
    t = Math.imul(t ^ (t >>> 15), t | 1);
    t ^= t + Math.imul(t ^ (t >>> 7), t | 61);
    return ((t ^ (t >>> 14)) >>> 0) / 4294967296;
  };
}

function seededShuffle<T>(arr: T[], rand: () => number): T[] {
  const copy = [...arr];
  for (let i = copy.length - 1; i > 0; i--) {
    const j = Math.floor(rand() * (i + 1));
    [copy[i], copy[j]] = [copy[j], copy[i]];
  }
  return copy;
}

function getLocalDayIndex(date: Date): number {
  const localMidnight = new Date(date.getFullYear(), date.getMonth(), date.getDate());
  return Math.floor(localMidnight.getTime() / DAY_MS);
}

function getDailySeed(date: Date): number {
  const y = date.getFullYear();
  const m = date.getMonth() + 1;
  const d = date.getDate();
  return y * 10000 + m * 100 + d;
}

function getCategorySlice(questions: TriviaQuestion[], date: Date): TriviaQuestion[] {
  const dayIndex = getLocalDayIndex(date);
  const cycle = Math.floor(questions.length / 8);
  const slot = dayIndex % cycle;
  const start = slot * 8;
  return questions.slice(start, start + 8);
}

export function getDailyTriviaCategories(date: Date = new Date()): TriviaCategory[] {
  const rand = mulberry32(getDailySeed(date) + DAILY_SEED + BANK_VERSION * 101);
  const shuffled = seededShuffle(CATEGORIES, rand);
  return shuffled.slice(0, 2);
}

export function getTriviaQuestions(categoryId: string, date: Date = new Date()): TriviaQuestion[] {
  const category = CATEGORIES.find((c) => c.id === categoryId);
  if (!category) return [];
  return getCategorySlice(category.questions, date);
}

export function getTriviaQuestionPools(
  categoryId: string,
  date: Date = new Date()
): Record<TriviaDifficulty, TriviaQuestion[]> {
  const category = CATEGORIES.find((c) => c.id === categoryId);
  if (!category) return { 1: [], 2: [], 3: [] };
  const slice = getCategorySlice(category.questions, date);
  const pools: Record<TriviaDifficulty, TriviaQuestion[]> = { 1: [], 2: [], 3: [] };
  for (const question of slice) {
    pools[question.difficulty].push(question);
  }
  return pools;
}

export function getTriviaCategory(id: string): TriviaCategory | undefined {
  return CATEGORIES.find((c) => c.id === id);
}

export default CATEGORIES;
